sysauto_script.script:
gs.info("Tricorp - PDF start", "Tricorp integration");
var _validFileExtensions = [".pdf"];
var pdfToDelete = [];

var fileList = new AzureUtils().getFileList(gs.getProperty('x_4s_tricode.pdf_directory'), _validFileExtensions);

gs.info("Tricorp - PDF " + fileList.length + " " + fileList[0], "Tricorp integration");

for (var i = 0; i < fileList.length; i++) {
	var gr = new GlideRecord('x_4s_tricode_tricode_application');

	gr.initialize();
	gr.u_comments = "Record from PDF import";

	var triCorpRecord = "" + gr.insert();

	if (triCorpRecord != "") {
		var fileInsert = new AzureUtils().getFile(fileList[i], gs.getProperty('x_4s_tricode.pdf_directory'), 'x_4s_tricode_tricode_application', triCorpRecord);

		gs.info("Tricorp - PDF insert " + fileInsert + " " + fileList[i], "Tricorp integration");

		if (fileInsert == 200 || fileInsert == 202) {
			pdfToDelete.push(fileList[i]);
		}

		new global.TricorpGlobalHelper().updateContentType("x_4s_tricode_tricode_application", triCorpRecord, "PDF");
	} else {
		gs.info("Tricorp - Tricord record creation failed", "Tricorp integration");
	}
}

deleteAzureFiles(pdfToDelete);

gs.info("Tricorp - PDF end", "Tricorp integration");

function deleteAzureFiles(azureFiles) {
	for (var i = 0; i < azureFiles.length; i++) {
		new AzureUtils().deleteFile(azureFiles[i], gs.getProperty('x_4s_tricode.pdf_directory'));
	}
}
